package com.example.milkman.Activity;

import android.content.Intent;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.View;
import android.widget.Button;

import com.example.milkman.Adapter.CartAdapter;
import com.example.milkman.Models.CartList;
import com.example.milkman.R;

import java.util.ArrayList;
import java.util.List;

public class CartActivity extends AppCompatActivity {
    RecyclerView recyclerView;
    private List<CartList> cartList = new ArrayList<>();
    CartAdapter testAdapter;
    CartList cl;
    Button checkout;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_cart);
        checkout = findViewById(R.id.checkout);

        testAdapter = new CartAdapter(getApplicationContext(), cartList);
        recyclerView = findViewById(R.id.recycler_cart);
        recyclerView.setHasFixedSize(true);
        recyclerView.setLayoutManager(new LinearLayoutManager(getApplicationContext()));

        checkout.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                startActivity(new Intent(getApplicationContext(), ConfirmationActivity.class));
            }
        });
        /*Intent i=getIntent();
        MilkList milkl =(MilkList) i.getSerializableExtra("sampleObject");
*/
        CartData();


        /*Bundle getBundle = this.getIntent().getExtras();
        getBundle.getParcelableArrayList("milkList");*/
    }

    public void CartData() {
        cartList.clear();

        cl = new CartList();
        cl.setPrice("20");
        cl.setTitle("Namdhari Fresh Milk");
        cl.setImageUrl("data:image/jpeg;base64,");
        cartList.add(cl);

        cl = new CartList();
        cl.setPrice("25");
        cl.setTitle("Patanjali");
        cl.setImageUrl("data:image/jpeg;base64,");
        cartList.add(cl);

        testAdapter = new CartAdapter(getApplicationContext(), cartList);
        recyclerView.setAdapter(testAdapter);
        testAdapter.notifyDataSetChanged();
    }
}
